    function random_rgba(i) {
          var o = Math.round, r = Math.random, s = 255;
          if(i==1){
            return 'rgba(' + o(r()*s) + ',' + o(r()*s) + ',' + o(r()*s) + ',' + r().toFixed(1) + ')';
          }else{
            return 'rgba(' + o(r()*s) + ',' + o(r()*s) + ',' + o(r()*s) + ',0.2)';
          }
    }
