body.script:
if(window.location.href.indexOf('rurales') !== -1){
    var buttonMenu = document.getElementById('button-menu');
    buttonMenu.parentNode.removeChild(buttonMenu);
    var divCiudad = document.getElementById('ciudad-title').parentElement.parentElement;
    divCiudad.parentNode.removeChild(divCiudad);
    var divMunicipales = document.getElementById('municipales-title').parentElement.parentElement;
    divMunicipales.parentNode.removeChild(divMunicipales);
    var divCategorias = document.getElementById('categorias-title').parentElement.parentElement;
    divCategorias.parentNode.removeChild(divCategorias);

    var buttonXLS = document.getElementById('button-xls');
    buttonXLS.parentNode.removeChild(buttonXLS);
    document.getElementById("button-limpiar").href = '/sede/portal/presupuestos-barrios-rurales/servicio/presupuestos-participativos/edicion/2?juntaContains=JV*';
};
