$(document).ready(function() {
    $(".ampliar.collapse").collapse();
    $(".masinfo").on('click', function() {
        if ($(this).find('.fa-chevron-down').length > 0) {
            $(this).find('.fa-chevron-down').removeClass('fa-chevron-down').addClass('fa-chevron-up');
        } else {
            $(this).find('.fa-chevron-up').removeClass('fa-chevron-up').addClass('fa-chevron-down');
        };
    });
});

$('#buscador').removeClass('hidden');
$("#junta").select2({
    ajax: {
        url: 'https://www.zaragoza.es/sede/servicio/distrito',
        dataType: 'json',
        type: "GET",
        quietMillis: 50,
        data: function (term) {
            return {
                fl : 'id,title',
                q: 'title==' + term.term + '*'
            };
        },
        processResults: function (data) {
            return {
                results: $.map(data.result, function (item) {
                    return {
                        text: item.title,
                        id: item.id
                    }
                })
            };
        }
    },
    minimumInputLength: 2,
    placeholder: 'Encuentra los mercados de tu Junta Municipal',
    allowClear: true,
});

$('#junta').on("select2:select", function(e) { 
    var item = e.params.data;
    console.log('select');
    $('.btn-filtro').removeClass('hidden').find('.texto').html(item.title);

    $(document).ready(function() {
        $('.juntas').attr("hidden", true);
        $('.juntas').each(function( index ) {
            if ($( this ).text().indexOf(item.text) >= 0) {
                $( this ).attr("hidden",false);
            }
        });

        //Mercados Públicos
        var numPublicos = 0;
        var numPublicosOcultos = 0;
        $('.publicos .juntas').each(function( index ) {
            numPublicos = numPublicos + 1;
            if ($( this ).is(':hidden')) {
                numPublicosOcultos = numPublicosOcultos + 1;
            }
        });
        if (numPublicos == numPublicosOcultos) {
            $('.publicos .alertJuntas').removeClass('hidden');
        }

        //Mercados Privados
        var numPrivados = 0;
        var numPrivadosOcultos = 0;
        $('.privados .juntas').each(function( index ) {
            numPrivados = numPrivados + 1;
            if ($( this ).is(':hidden')) {
                numPrivadosOcultos = numPrivadosOcultos + 1;
            }
        });
        if (numPrivados == numPrivadosOcultos) {
            $('.privados .alertJuntas').removeClass('hidden');
        }
    });
});
$('#junta').on("select2:unselect", function(e) { 
    var data = e.params.data;
    console.log('unselect');
    $('#junta').typeahead('val', '');
    $('.juntas').attr("hidden", false);
    $('.alertJuntas').addClass("hidden");
    $('.btn-filtro').addClass('hidden').find('.texto').empty();
});
