$(function() {
    $("#rscont .collapse").collapse();
});

// Navegación por teclado. Apertura de bloques mediante tecla enter
$(".masinfo").keypress(function(e) {
    if (e.keyCode == 13) {
        var id = $(this).attr('data-target');
        console.log(id);
        console.log($(id));
        $(id).collapse('toggle');
    }
  });
